    (
        #{cmpnyId, jdbcType=VARCHAR}
      , #{splCmpnyId, jdbcType=VARCHAR}
      , #{aplStrtDt, jdbcType=VARCHAR}
      , #{memoSeq, jdbcType=NUMERIC}
      , #{memoCont, jdbcType=VARCHAR}
